import React, { Component } from "react";

class MessageForm extends Component {
  state = {
    text: ".0...."
  };

  onChange = e => {
    this.setState({ text: e.target.value });
  };
  render() {
    return (
      <div>
        <input type="text" value={this.state.text} onChange={this.onChange} />
        <button>send</button>
      </div>
    );
  }
}

export default MessageForm;
