import React, { Component } from "react";
import socketIOClient from "socket.io-client";
import MessageList from "./MessageList";
import MessageForm from "./MessageForm";
// import Message from "./Message";

class Chatroom extends Component {
  state = {
    message: [
      { text: "Hi", member: "FutureSkill" },
      { text: "Hi", member: "Mark" },
      { text: "Good Bye", member: "FutureSkill" },
      { text: "Bye", member: "Mark" }
    ]
  };

  onMessageSend = message => {
    this.setState({ message: [...this.state.message, { ...message }] });
  };

  render() {
    return (
      <div>
        <MessageList message={this.state.message} />
        <MessageForm onMessageSend={this.onMessageSend} />
      </div>
    );
  }
}

export default Chatroom;
